-- Создание таблицы статей
CREATE TABLE IF NOT EXISTS `articles` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `text` longtext NOT NULL,
  `author_id` int(11) NOT NULL,
  `created_at` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `author_id` (`author_id`),
  CONSTRAINT `articles_ibfk_1` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- Создание таблицы комментариев
CREATE TABLE IF NOT EXISTS `comments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `text` text NOT NULL,
  `article_id` int(11) NOT NULL,
  `parent_comment_id` int(11) DEFAULT NULL,
  `author_id` int(11) NOT NULL,
  `created_at` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `article_id` (`article_id`),
  KEY `parent_comment_id` (`parent_comment_id`),
  KEY `author_id` (`author_id`),
  CONSTRAINT `comments_ibfk_1` FOREIGN KEY (`article_id`) REFERENCES `articles` (`id`) ON DELETE CASCADE,
  CONSTRAINT `comments_ibfk_2` FOREIGN KEY (`parent_comment_id`) REFERENCES `comments` (`id`) ON DELETE CASCADE,
  CONSTRAINT `comments_ibfk_3` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- Пример данных для статей
INSERT INTO `articles` (`id`, `title`, `description`, `text`, `author_id`, `created_at`) VALUES
(1, 'История развития интернета', 'От ARPANET до современных технологий', 'Первые разработки интернета начались в конце 1960-х годов с появления ARPANET — сети, финансируемой Министерством обороны США. Изначально она соединяла компьютеры нескольких университетов и исследовательских центров.\n\nВ 1980-х Тим Бернерс-Ли разработал World Wide Web, добавив графический интерфейс и гипертекст, что сделало интернет доступным для широкой публики. С появлением первых коммерческих браузеров в 1990-х началось стремительное распространение сети.\n\nСовременный интернет — это глобальная сеть, включающая миллиарды устройств и охватывающая почти все сферы человеческой деятельности.', 1, '2023-03-15 12:00:00'),
(2, 'Тренды фронтенд-разработки 2023', 'Технологии, которые меняют будущее веб-разработки', 'React остаётся самым популярным фреймворком, но на пятки ему наступают Vue и Angular. Микрофронтенды становятся стандартным подходом для крупных компаний, позволяя разным командам независимо разрабатывать части одного сайта.\n\nTailwind CSS продолжает завоёвывать популярность благодаря утилитарному подходу к стилизации. WebAssembly открывает новые возможности для запуска высокопроизводительного кода прямо в браузере.\n\nС развитием API и бэкенда как сервиса (BaaS) всё больше разработчиков переходят на JAMstack архитектуру, разделяя фронтенд и бэкенд для лучшей производительности и безопасности.', 1, '2023-03-20 14:30:00'),
(3, 'Кибербезопасность в эпоху IoT', 'Новые вызовы и защита устройств интернета вещей', 'Умные устройства окружают нас повсюду: от холодильников и телевизоров до промышленного оборудования. По прогнозам, к 2025 году количество IoT-устройств превысит 75 миллиардов. Но вместе с удобством приходят и риски.\n\nБольшинство IoT-устройств имеют слабую защиту: стандартные пароли, незашифрованный трафик, отсутствие обновлений. Хакеры могут не только получить доступ к данным, но и превратить устройства в ботнеты для DDoS-атак.\n\nДля защиты специалисты рекомендуют менять стандартные пароли, регулярно обновлять прошивку, использовать отдельные сети для IoT-устройств и внедрять многофакторную аутентификацию везде, где это возможно.', 2, '2023-03-25 10:15:00');

-- Пример данных для комментариев
INSERT INTO `comments` (`id`, `text`, `article_id`, `parent_comment_id`, `author_id`, `created_at`) VALUES
(1, 'Интересный факт: первый опубликованный в интернете текст был отправлен 29 октября 1969 года, и это было слово "LOGIN", но система рухнула после первых двух букв.', 1, NULL, 2, '2023-03-15 14:20:00'),
(2, 'Не упомянули WebRTC, которая сейчас активно развивается и позволяет создавать видеозвонки без установки дополнительного ПО.', 2, NULL, 3, '2023-03-16 09:45:00'),
(3, 'Да, WebRTC действительно стоило упомянуть. Особенно учитывая, что теперь её поддерживают все основные браузеры.', 2, 2, 1, '2023-03-16 10:30:00'),
(4, 'Вы правы. К тому же, с развитием 5G технологий стриминговые возможности WebRTC становятся ещё более значимыми.', 2, 3, 1, '2023-03-16 11:00:00'),
(5, 'А что думаете насчет WebTransport? Вроде как должен в скором времени заменить WebSockets.', 2, 4, 2, '2023-03-16 12:15:00'),
(6, 'Меня пугает, что большинство производителей IoT устройств не заботятся о безопасности, а потребители даже не задумываются об этом при покупке.', 3, NULL, 3, '2023-03-21 08:30:00'),
(7, 'Недавно прочитал, что хакеры взломали аквариум в казино через IoT-термометр и получили доступ к базе данных клиентов. Реальность страшнее любой фантастики.', 3, NULL, 1, '2023-03-25 16:40:00'); 